mapper.select:
CREATE TABLE blog_tweet ( id bigint NOT NULL AUTO_INCREMENT, content text, author varchar(30), publish_time datetime, update_time datetime, PRIMARY KEY (id) ) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_bin;
CREATE TABLE blog_tweet_pic ( id bigint NOT NULL AUTO_INCREMENT, tweet_id varchar(20), pic_type char(2), pic_path varchar(100), PRIMARY KEY (id) ) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_bin;
CREATE TABLE blog_tweet_comment ( id bigint NOT NULL AUTO_INCREMENT, tweet_id varchar(20), content varchar(300), pic_path varchar(100), publish_time datetime, PRIMARY KEY (id) ) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_bin;
